/* utf8格式汉字转化为ascii格式 */
static inline uint32_t chinese_utf8_to_ascii(uint8_t *src, uint32_t src_len, uint8_t *dst, uint32_t *dst_len)
{
    uint32_t i;
    uint32_t offset = 0;
    
    for (i = 0; i < src_len; i += 3)
    {
        dst[offset] = src[i];
        dst[offset + 1] = src[i+1];
        offset += 2;
    }

    *dst_len = offset;
    return offset;
}

